Insert into dgva_study_browser (STUDY_ACCESSION,TAXONOMY_IDS,SCIENTIFIC_NAMES,COMMON_NAMES,PUBMED_IDS,DISPLAY_NAME,STUDY_TYPE,STUDY_URL,STUDY_DESCRIPTION,ANALYSIS_TYPES,DETECTION_METHODS,METHOD_TYPES,PLATFORM_NAMES,ASSEMBLY_NAMES,ASSEMBLY_ACCESSIONS) values ('nstd117','9823','Sus scrofa','Pig','27028052','Long_et_al_2016','Case-Control',null,'We performed a case-control genome-wide CNV association study of Umbilical hernia (UH) on 905 pigs from Duroc, Landrace and Yorkshire breeds using the Porcine SNP60 BeadChip and PennCNV algorithm. We firstly constructed a genomic map comprising 6,193 CNVs that pertain to 737 CNV regions (CNVRs). Then, we identified eight CNVs significantly associated with the risk for UH in the three pig breeds. Six of seven significantly associated CNVs were validated using quantitative real-time PCR. Notably, a rare CNV (CNV14:13030843-13059455) encompassing the NUGGC gene, implicated in human omphalocele and inguinal hernia, was strongly associated with UH (Permutation-corrected P = 0.0015) in Duroc pigs.','SNP genotyping analysis,Probe signal intensity',null,'SNP array,qPCR','Infinium II Multisample assay platform (Illumina),Not reported','Sscrofa10.2','GCF_000003025.5');
